import logo from "../../images/logo.svg";
import { Link, useLocation } from "react-router-dom";



export default function Header({ isLoggedIn, logOut }) {
  const locate = useLocation();
  return (
    <header className="header">
      <img className="header__logo" alt="логотип" src={logo} />
      {isLoggedIn ? (
       (<className>
       <p className="header__auth-button"> email </p>
       <Link className="header__logout-button" to='/sign-in' onClick={logOut}> Выйти</Link>
       </className>)
      ) : 
        <Link className="header__auth-button"   to={locate.pathname === "/sign-in" ? "/sign-up" : "/sign-in"}> {locate.pathname === "/sign-in" ? "Регистрация" : "Войти"}   </Link>}



    </header>
  );
}
