import Header from "./header/Header.jsx";
import Main from "./main/Main.jsx";
import Footer from "./footer/Footer.jsx";
import PopupWithForm from "./popupWithForm/PopupWithForm.jsx";
import ImagePopup from "./imagePopup/ImagePopup.jsx";
import { useState } from "react";
import { useCallback } from "react";
import CurrentUserContext from "../contexts/CurrentUserContext";
import { useEffect } from "react";
import api from "../utils/api.js";
import EditProfilePopup from "./editProfilePopup/EditProfilePopup";
import EditAvatarPopup from "./editAvatarPopup/EditAvatarPopup";
import AddPlacePopup from "./addPlacePopup/AddPlacePopup";
import Login from "./login/Login.jsx";
import Register from "./register/Register.jsx";
import ProtectedRoute from "./protectedRoute/ProtectedRoute.jsx";
import { Route, Routes, Navigate, useNavigate } from "react-router-dom";
import * as auth from "../utils/auth";
import InfoTooltip from "./infoTooltip/InfoTooltip.jsx";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isQuestionPopupOpen, setIsQuestionPopupOpen] = useState(false);
  const [isTooltipPopupOpen, setIsTooltipPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImgPopupOpen, setIsImgPopupOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({});
  const [deleteCardId, setDeleteCardId] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const [isLoggedIn, setisLoggedIn] = useState(false);
  const [isSuccessed, setIsSuccessed] = useState(false);

  const navigate = useNavigate();

  const [cards, setCards] = useState([]);

  const [userEmail, setEmail] = useState("");

  const setClosedAllPopups = useCallback(() => {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsImgPopupOpen(false);
    setIsQuestionPopupOpen(false);
    setIsTooltipPopupOpen(false);
  }, []);

  const closePopupOnEsc = useCallback(
    (evt) => {
      if (evt.key === "Escape") {
        setClosedAllPopups();
        document.removeEventListener("keydown", closePopupOnEsc);
      }
    },
    [setClosedAllPopups]
  );

  const closeAllPopups = useCallback(() => {
    setClosedAllPopups();
    document.removeEventListener("keydown", closePopupOnEsc);
  }, [setClosedAllPopups, closePopupOnEsc]);

  function setEventListenerForAll() {
    document.addEventListener("keydown", closePopupOnEsc);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    setEventListenerForAll();
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
    setEventListenerForAll();
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
    setEventListenerForAll();
  }

  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImgPopupOpen(true);
    setEventListenerForAll();
  }

  function deleteCard(evt) {
    evt.preventDefault();
    setIsUpdating(true);
    api
      .deleteCard(deleteCardId)
      .then(() => {
        setCards(
          cards.filter((card) => {
            return card._id !== deleteCardId;
          })
        );
        closeAllPopups();
        setIsUpdating(false);
      })
      .catch((error) => console.error(`Ошибка: ${error}`));
  }

  function handleCardDeleteClick(cardId) {
    setDeleteCardId(cardId);
    setIsQuestionPopupOpen(true);
    setEventListenerForAll();
  }

  function handleLikeClick(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    isLiked
      ? api
          .removeLike(card._id)
          .then((newCard) => {
            setCards((state) =>
              state.map((c) => (c._id === card._id ? newCard : c))
            );
          })
          .catch((error) => console.error(`Ошибка: ${error}`))
      : api
          .addLike(card._id)
          .then((newCard) => {
            setCards((state) =>
              state.map((c) => (c._id === card._id ? newCard : c))
            );
          })
          .catch((error) => console.error(`Ошибка: ${error}`));
  }

  function handleUpdateUser({ name, about }) {
    api
      .setProfile(name, about)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((error) => console.error(`Ошибка: ${error}`));
  }

  function handleUpdateAvatar({ avatar }) {
    api
      .setAvatarImg(avatar)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((error) => console.error(`Ошибка: ${error}`));
  }

  function handleAddPlaceSubmit({ title, link }) {
    api
      .setCard(title, link)
      .then((res) => {
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch((error) => console.error(`Ошибка: ${error}`));
  }

  useEffect(() => {
    Promise.all([api.getProfile(), api.getCards()])
      .then(([dataUser, dataCard]) => {
        setCurrentUser(dataUser);
        setCards(dataCard);
        //  setLikes(dataCard.likes);
      })
      .catch((error) => console.log(`Ошибка: ${error}`));
  }, []);

  function checkToken() {
    const token = localStorage.getItem("token");
    if (token) {
      auth
        .checkToken(token)
        .then((res) => {
          if (res && res.data) {
            setisLoggedIn(true);
            setEmail(res.data.emaill);
            navigate("/");
          }
        })
        .catch((err) => {
          console.log("Ошибка: ", err);
          setisLoggedIn(false);
        });
    }
  }
  useEffect(() => {
    checkToken();
  }, []);

  function handleLogin(loginData) {
    auth
      .login(loginData)
      .then((res) => {
        localStorage.setItem("token", res.token);
        setEmail(loginData.email);
        setisLoggedIn(true);
        navigate("/");
        console.log(res);
      })
      .catch((error) => {
        console.error("Ошибка:", error);
        setIsTooltipPopupOpen(true);
        setIsSuccessed(false);
      });
  }

  function handleRegister(regData) {
    auth
      .register(regData)
      .then((res) => {
        if (res && res.data) {
          navigate("/sign-in");
   setIs
          setIsInfoTooltipPopup(true);
        }
      })
      .catch((err) => {
        setRegSuccess(false);
        setIsInfoTooltipPopup(true);
        console.log("Ошибка регистрации:", err);
        console.log(regData);
      });
  }


/*   function handleRegister(data) {
    auth
      .register(data.email, data.password)
      .then(() => {
        setEmail(data.email);
        setPassword(data.password);
        setIsSuccessed(true);
        setIsTooltipPopupOpen(true);
        navigate("/sign-in");
        console.log("done");
      })
      .catch((err) => {
        setIsSuccessed(false);
        setIsTooltipPopupOpen(true);
        console.log("Ошибка регистрации:", err);
        console.log(data);
      });
  } */

  function handleLogoutClick() {
    localStorage.removeItem("token");
    setisLoggedIn(false);
    navigate("/sign-in");
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page__container">
        <Header isLoggedIn={isLoggedIn} logOut={handleLogoutClick} />
        <Routes>
          <Route
            path="/"
            element={
              <ProtectedRoute
                element={Main}
                isLoggedIn={isLoggedIn}
                onEditProfile={handleEditProfileClick}
                onAddPlace={handleAddPlaceClick}
                onEditAvatar={handleEditAvatarClick}
                onCardClicked={handleCardClick}
                cards={cards}
                onCardLike={handleLikeClick}
                onDelete={handleCardDeleteClick}
              />
            }
          />

          <Route
            path="/sign-up"
            element={
              <Register
                onRegister={handleRegister}
                btnText="Зарегистрироваться"
                /*  onTogglePage={handleTogglePage} */
              />
            }
          />

          <Route
            path="/sign-in"
            element={
              <Login
                onLogin={handleLogin}
                email={userEmail}
              />
            }
          />

          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
        {/*     <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClicked={handleCardClick}
          cards={cards}
          onCardLike={handleLikeClick}
          onDelete={handleCardDeleteClick}
        /> */}
        <Footer />

        <InfoTooltip
          isOpen={isTooltipPopupOpen}
          onClose={closeAllPopups}
          authSuccessed={isSuccessed}
        />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
          isUpdating={setIsUpdating}
        />

        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          isUpdating={isUpdating}
          onAddPlace={handleAddPlaceSubmit}
        />

        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />

        <PopupWithForm
          name="delete-card"
          title="Вы уверены?"
          button="Да"
          isOpen={isQuestionPopupOpen}
          onClose={closeAllPopups}
          onSubmit={deleteCard}
          isUpdating={isUpdating}
        />

        <ImagePopup
          card={selectedCard}
          isOpen={isImgPopupOpen}
          onClose={closeAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
