import { useContext } from "react";
import PopupWithForm from "../popupWithForm/PopupWithForm";
import CurrentUserContext from "../../contexts/CurrentUserContext.js";
import { useEffect } from "react";


export default function EditProfilePopup( { isOpen, onClose }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');  

  const currentUser = useContext(CurrentUserContext)

    useEffect(() => {
      setName(currentUser.name);
      setDescription(currentUser.about);
    }, [currentUser]); 

    function handleSubmit(e) {
      e.preventDefault();
      onUpdateUser({
        name,
        about: description,
      });
    }

    return (
        <PopupWithForm
        name="edit-profile"
        title="Редактировать профиль"
        button="Сохранить"
        isOpen={isOpen}
        onClose={onClose}
      //  isUpdating={isUpdating}
      >
        <fieldset className="popup__set">
          <label className="popup__form-label">
            <input
              type="text"
              className="popup__item popup__item_type_username"
              placeholder="Имя"
              defaultValue=""
              name="edit__username"
              required=""
              minLength={2}
              maxLength={40}
              id="name"
            />
            <span className="popup__input-error name-error" />
          </label>
          <label className="popup__form-label">
            <input
              type="text"
              className="popup__item popup__item_type_description"
              placeholder="Вид деятельности"
              defaultValue=""
              name="edit__description"
              required=""
              minLength={2}
              maxLength={200}
              id="description"
            />
            <span className="popup__input-error description-error" />
          </label>
        </fieldset>{" "}
      </PopupWithForm>
    )
}
