import logo from "../logo.svg";
import Header from "./header/Header.jsx";
import Main from "./main/Main.jsx";
import Footer from "./footer/Footer.jsx";
import PopupWithForm from "./popupWithForm/PopupWithForm.jsx";
import ImagePopup from "./imagePopup/ImagePopup.jsx";
import { useState } from "react";
import { useCallback } from "react";
import CurrentUserContext from "../contexts/CurrentUserContext";
import { useEffect } from "react";
import api from "../utils/api.js";
import EditProfilePopup from "./editProfilePopup/EditProfilePopup";
import EditAvatarPopup from "./editAvatarPopup/EditAvatarPopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isQuestionPopupOpen, setIsQuestionPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImgPopupOpen, setIsImgPopupOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({});
  const [deleteCardId, setDeleteCardId] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  const [cards, setCards] = useState([]);

  const setClosedAllPopups = useCallback(() => {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsImgPopupOpen(false);
    setIsQuestionPopupOpen(false);
  }, []);

  const closePopupOnEsc = useCallback(
    (evt) => {
      if (evt.key === "Escape") {
        setClosedAllPopups();
        document.removeEventListener("keydown", closePopupOnEsc);
      }
    },
    [setClosedAllPopups]
  );

  const closeAllPopups = useCallback(() => {
    setClosedAllPopups();
    document.removeEventListener("keydown", closePopupOnEsc);
  }, [setClosedAllPopups, closePopupOnEsc]);

  function setEventListenerForAll() {
    document.addEventListener("keydown", closePopupOnEsc);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    setEventListenerForAll();
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
    setEventListenerForAll();
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
    setEventListenerForAll();
  }

  function handleCardClick(card) {
    setSelectedCard(card);
    setIsImgPopupOpen(true);
    setEventListenerForAll();
  }

  function deleteCard(evt) {
    evt.preventDefault();
    setIsUpdating(true);
    api
      .deleteCard(deleteCardId)
      .then(() => {
        setCards(
          cards.filter((card) => {
            return card._id !== deleteCardId;
          })
        );
        closeAllPopups();
        setIsUpdating(false);
      })
      .catch((error) => console.error(`Ошибка: ${error}`));
  }

  function handleCardDeleteClick(cardId) {
    setDeleteCardId(cardId);
    setIsQuestionPopupOpen(true);
    setEventListenerForAll();
  }

  function handleLikeClick(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    isLiked
      ? api
          .removeLike(card._id)
          .then((newCard) => {
            setCards((state) =>
              state.map((c) => (c._id === card._id ? newCard : c))
            );
          })
          .catch((error) => console.error(`Ошибка: ${error}`))
      : api
          .addLike(card._id)
          .then((newCard) => {
            setCards((state) =>
              state.map((c) => (c._id === card._id ? newCard : c))
            );
          })
          .catch((error) => console.error(`Ошибка: ${error}`));
  }



  function handleUpdateUser({ name, about }) {
    api.setProfile(name, about).then(res => { setCurrentUser(res); closeAllPopups() }).catch((error) => console.error(`Ошибка: ${error}`));
  }

  function handleUpdateAvatar({ avatar }) {
    api.setAvatarImg(avatar).then(res => { setCurrentUser(res); closeAllPopups() }).catch((error) => console.error(`Ошибка: ${error}`));
  }

  useEffect(() => {
    Promise.all([api.getProfile(), api.getCards()])
      .then(([dataUser, dataCard]) => {
        setCurrentUser(dataUser);
        setCards(dataCard);
        //  setLikes(dataCard.likes);
      })
      .catch((error) => console.log(`Ошибка: ${error}`));
  }, []);

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page__container">
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClicked={handleCardClick}
          cards={cards}
          onCardLike={handleLikeClick}
          onDelete={handleCardDeleteClick}
        />
        <Footer />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />

        <PopupWithForm
          name="add-place"
          title="Новое место"
          button="Создать"
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          isUpdating={isUpdating}
        >
          <fieldset className="popup__set">
            <label className="popup__form-label">
              <input
                type="text"
                className="popup__item popup__item_type_place-name"
                placeholder="Название"
                defaultValue=""
                name="place__name"
                id="place"
                required=""
                minLength={2}
                maxLength={30}
              />
              <span className="popup__input-error place-error" />
            </label>
            <label className="popup__form-label">
              <input
                type="url"
                className="popup__item popup__item_type_link"
                placeholder="Ссылка на картинку"
                defaultValue=""
                name="place__link"
                id="link"
                required=""
              />
              <span className="popup__input-error link-error" />
            </label>
          </fieldset>{" "}
        </PopupWithForm>

        <Ed
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        >

        </Ed>

        <PopupWithForm
          name="delete-card"
          title="Вы уверены?"
          button="Да"
          isOpen={isQuestionPopupOpen}
          onClose={closeAllPopups}
          onSubmit={deleteCard}
          isUpdating={isUpdating}
        />

        <ImagePopup
          card={selectedCard}
          isOpen={isImgPopupOpen}
          onClose={closeAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
