import { useEffect } from "react";
import { useContext } from "react";
import { useState } from "react";
import CurrentUserContext from "../../contexts/CurrentUserContext.js";
import api from "../../utils/api.js";
import Card from "../card/Card.jsx";

export default function Main({
  onEditProfile,
  onAddPlace,
  onEditAvatar,
  onCardClicked,
}) {
const currentUser = useContext(CurrentUserContext)

  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [avatar, setAvatar] = useState("");
  const [cards, setCards] = useState([]);
  const [likes, setLikes] = useState("");

  useEffect(() => {
    Promise.all([api.getProfile(), api.getCards()])
      .then(([dataUser, dataCard]) => {
        setName(dataUser.name);
        setDescription(dataUser.about);
        setAvatar(dataUser.avatar);
        dataCard.forEach((data) => (data.myid = dataUser._id));
        setCards(dataCard);
        setLikes(dataCard.likes);
      })
      .catch((error) => console.log(`Ошибка: ${error}`));
  }, []);

  return (
    <main className="main">
      <section className="profile">
        <div className="profile__profile-info">
          <img
            className="profile__avatar"
            alt="аватар пользователя"
            src={avatar}
          />
          <button
            className="profile__overlay-button"
            type="button"
            onClick={onEditAvatar}
          />
          <div className="profile__name-section">
            <div className="profile__title">
              <h1 className="profile__name">{name}</h1>
              <button
                className="profile__edit-button"
                type="button"
                onClick={onEditProfile}
              />
            </div>
            <p className="profile__subtitle">{description}</p>
          </div>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={onAddPlace}
        />
      </section>
      <section className="elements">
        {cards.map((data) => {
          return (
            <Card key={data._id} card={data} onCardClicked={onCardClicked} />
          );
        })}
      </section>
    </main>
  );
}
