import PopupWithForm from "../popupWithForm/PopupWithForm"
import { useEffect, useState } from "react";

export default function AddPlacePopup({isOpen, onClose, isUpdating, onAddPlace}) {

    const [formValues, setFormValues] = useState({ title: '', link: '' })

    function handleChange(e) {
      const { name, value } = e.target;
      setFormValues({ ...formValues, [name]: value })
    }

    useEffect(() => {
      setFormValues({ title: '', link: '' })
    }, [isOpen]);

    function handleSubmit(e) {
      e.preventDefault();
      onAddPlace({ title: formValues.title, link: formValues.link });

    }

    return (
        <PopupWithForm
        name="add-place"
        title="Новое место"
        button="Создать"
        isOpen={isOpen}
        onClose={onClose}
        isUpdating={isUpdating}

      >
        <fieldset className="popup__set">
          <label className="popup__form-label">
            <input
              type="text"
              className="popup__item popup__item_type_place-name"
              placeholder="Название"
              defaultValue=""
              name="place__name"
              id="place"
              required=""
              minLength={2}
